var show = document.getElementById('showMenu');
var hide = document.getElementById('hideMenu');
var navLink = document.getElementById('navLinks');

function showMenu() {
    show.style.display = 'none';
    navLink.style.transform = 'translateY(0)'
    hide.style.display = 'block';
}
function hideMenu() {
    show.style.display = 'block';
    navLink.style.transform = 'translateY(-100%)'
    hide.style.display = 'none';
}